CREATE POLICY "Admins can insert class_students" ON class_students FOR INSERT TO authenticated WITH CHECK ((SELECT role FROM users WHERE id = auth.uid()) = 'admin'); CREATE POLICY "Admins can delete class_students" ON class_students FOR DELETE TO authenticated USING ((SELECT role FROM users WHERE id = auth.uid()) = 'admin'); CREATE POLICY "Teachers can insert class_students" ON class_students FOR INSERT TO authenticated WITH CHECK ((SELECT role FROM users WHERE id = auth.uid()) = 'teacher'); CREATE POLICY "Teachers can delete class_students" ON class_students FOR DELETE TO authenticated USING ((SELECT role FROM users WHERE id = auth.uid()) = 'teacher');;
